    private void jButton6ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton6ActionPerformed
    //Butler county button
    try{    
    float num1, taxRate, tax, result;

    num1 = Float.parseFloat(jTextField1.getText());
    taxRate = (float) 0.0625;
    tax = num1 * taxRate;
    result = num1+tax;

    jTextField3.setText(String.valueOf(result));
    } catch (NumberFormatException nfe) {
            JOptionPane.showMessageDialog(null,"Sale Total must be a numerical value!!!"
                    + " \n\nNO LETTERS!!!\n\nNO BLANKS!!! \n \nNUMBERS ONLY!!!");
    }
    }//GEN-LAST:event_jButton6ActionPerformed

    private void jButton2ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton2ActionPerformed
        //clear group button
        jTextField1.setText("");
        jTextField2.setText("");
        jTextField3.setText("");
        totals.removeAll(totals);
    }//GEN-LAST:event_jButton2ActionPerformed

