    private void jButton4ActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_jButton4ActionPerformed
        // add customer to group total button
        try{
        
        float num1;
        num1 = Float.parseFloat(jTextField1.getText());
        totals.add(num1);
        
        float groupTotal = 0;
        for (float element : totals)
        { 
            groupTotal= groupTotal + element;
            jTextField2.setText(String.valueOf(groupTotal));
        }   
      
        
    }//GEN-LAST:event_jButton4ActionPerformed
        catch (NumberFormatException nfe) {
            JOptionPane.showMessageDialog(null,"Sale Total must be a numerical value!!!!"
                    + " \n\nNO LETTERS!!!\n\nNO BLANKS!!! \n \nNUMBERS ONLY!!!");
        }
    }
